
public class APcircle {
	
	public static double r=4.5;  //declaring 

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double perimeter = 2*Math.PI*r; // calculating perimeter and area of circle
		double area= Math.PI*r*r;
		
		System.out.println("Perimeter Of Circle Is="+perimeter);
		System.out.println("Area Of Circle Is="+area);			//printing result
	}

}
